import React, { useState } from "react";
import "./qualification.css";

export default function Education ({keyValuePairs}){
  const [toggleState, setToggleState] = useState(1);
  const [dividerLine, setDividerLine] = useState(0);
  function toCreateDivider(){
    setDividerLine(dividerLine+1);
  }

  return (
    <div>
          <div
            className={toggleState === 1 ? "qualification__button qualification__active button--flex": "qualification__button button--flex"}
            onClick={() => setToggleState(1)}>
            <i className="uil uil-graduation-cap qualification__icon"></i>Education
          </div>

          <div className={toggleState === 2? "qualification__button qualification__active button--flex": "qualification__button button--flex"}
               onClick={() => setToggleState(2)}>
            <i className="uil uil-briefcase-alt qualification__icon"></i>Experience
          </div>

        <div className="qualification__sections">
          <div className={toggleState === 1? "qualification__content qualification__content-active": "qualification__content"}>

            {keyValuePairs.map(([key, value])=> (
               <div>
                    <div className="qualification__data">
                      {(dividerLine%2) ===1 ?
                          <div>
                              <div></div>
                              <div>
                                 <span className="qualification__rounder"></span>
                                 <span className="qualification__line"></span>
                               </div>
                          </div>
                      :''}
                      <div>
                        <h3 className="qualification__title">{key}</h3>
                        <span className="qualification__subtitle"><i class="uil uil-map-pin"></i>{value[0]}</span>
                        <br/>
                        <span className="qualification__subtitle"><i class="uil uil-graduation-cap"></i>{value[1]}</span>
                        <br/>
                        <div className="qualification__calender"><i className="uil uil-calendar-alt"></i>{value[2]}</div>
                      </div>
                        {(dividerLine%2) ===0 ?
                          <div>
                             <span className="qualification__rounder"></span>
                             <span className="qualification__line"></span>
                          </div>
                        :''}
                    </div>
                    {toCreateDivider}
               </div>
            ))}
          </div>

          <div className={toggleState === 2? "qualification__content qualification__content-active": "qualification__content"}>

          </div>
        </div>
    </div>
  );
}
